<script>
export default {
  name: "stats-tag",
  props: {
    rank: Number,
    stats: Number,
    name: String,
    // botId: Number
    // 0: Dialog flow
    // 1: Pandorabots
    // 2: Rasa
  },
  computed: {
    color () {
      if (this.rank === 1) {
        return "rgb(237, 179, 108)";
      } else if (this.rank === 2) {
        return "rgb(122, 122, 122)";
      } else {
        return "rgb(193, 133, 92)";
      }
    },
    botIconStyle () {
      if (this.rank === 1) {
        return {left: '20%', right: '20%', top: '15px'};
      } else if (this.rank === 2) {
        return {left: '25%', right: '25%', top: '15px'};
      } else {
        return {left: '27%', right: '27%', top: '20px'};
      }
    }
  },
  mounted() {
    this.$refs.stats.style.color = this.color;
  }
}
</script>

<style scoped lang="less">
.stats {
  height: 100%;
  display: inline-block;
  text-align: center;
}
.main {
  position: relative;
  .botIcon {
    position: absolute;
  }
}
</style>
